"use strict";var initCatalogFiltersScripts=function(){var t=document.querySelectorAll(".custom-checkbox"),e=document.querySelectorAll(".catalog-filter__top"),c=document.querySelectorAll(".catalog-filter");document.querySelector(".catalog-filters__btn").addEventListener("click",function(){c.forEach(function(t){t.closest(".catalog-filter").classList.toggle("catalog-filter--show")})}),e.forEach(function(t){t.addEventListener("click",function(){t.closest(".catalog-filter").classList.toggle("catalog-filter--open"),t.querySelector(".catalog-filter__title").classList.toggle("catalog-filter__title--active")})}),t.forEach(function(e){e.addEventListener("click",function(t){t.preventDefault(),e.classList.toggle("custom-checkbox--active")})})};window.addEventListener("load",function(){initCatalogFiltersScripts()});